import "./App.css";
import { Routes, Route } from "react-router-dom";
import Shelf from "./Shelf"
import SearchBooks from "./SearchBooks"
import * as BooksAPI from "./BooksAPI";
import { useState, useEffect } from 'react';

function App() {
    const [books, setBooks] = useState([]);

    const [currentlyReadingBooks, setCurrentlyReadingBooks] = useState([]);
    const [wantToReadBooks, setWantToReadBooks] = useState([]);
    const [readBooks, setReadBooks] = useState([]);



    const categorizeBooks = () => {
        console.log("inside categorizeBooks")

        books.map((book) => {
            console.log(book.shelf)
            if (book.shelf === "currentlyReading") {
                console.log({book})
                setCurrentlyReadingBooks(currentlyReadingBooks.concat(book));
            }
            else if (book.shelf === "wantToRead") {
                setWantToReadBooks(wantToReadBooks.concat(book));
            }
            else if (book.shelf === "read") {
                setReadBooks(readBooks.concat(book));
            }
        })
    };

    useEffect(() => {
        const getAllBooks = async () => {
            const res = await BooksAPI.getAll().then(() => {
                console.log({res});
                setBooks(res);
                categorizeBooks();
            });
        }
        getAllBooks();

    }, [])

    return (
        <Routes>
            <Route exact path="/" element={
                <Shelf/>
            }/>
            <Route path="/search" element={
                <SearchBooks />
            }/>
        </Routes>
    );
}


export default App;
