import { useEffect } from "react";
import { Routes, Route, Link } from "react-router-dom";

import Bookshelf from "./Bookshelf";
import SearchBooks from "./SearchBooks";

const MyRead = ({ books, setBooks }) => {
    const handleBookshelfUpdate = (book) => {
        let bookInAShelf = false;
        setBooks( () => {
            books.map((b) => { 
                if (b.id === book.id ) {
                    bookInAShelf = true;
                    return { ...b, shelf: book.shelf };
                }
            })
            if (!bookInAShelf) {
                setBooks(books.concat(book));
            }
        });
    };

  useEffect(()=> {}, [books]);

  return (
    <Routes>
      <Route
        exact
        path="/"
        element={
          <div className="app">
            <div className="list-books">

              <div className="list-books-title">
                <h1>MyRead</h1>
              </div>

              <div className="list-books-content">
                <Bookshelf
                  title="Currently Reading"
                  bookshelfCode="currentlyReading"
                  books={books}
                  onBookshelfUpdate={handleBookshelfUpdate}
                />
                <Bookshelf
                  title="Want To Read"
                  bookshelfCode="wantToRead"
                  books={books}
                  onBookshelfUpdate={handleBookshelfUpdate}
                />
                <Bookshelf
                  title="Read"
                  bookshelfCode="read"
                  books={books}
                  onBookshelfUpdate={handleBookshelfUpdate}
                />
              </div>
            </div>

            <div className="open-search">
              <Link to="/search">
                Add a book
              </Link>
            </div>
          </div>
        }
      />
      <Route path="/search" element={<SearchBooks books={books} onBookshelfUpdate={handleBookshelfUpdate}/>} />
    </Routes>
  );
};

export default MyRead;
