import { Link } from "react-router-dom";
import BooksGrid from "./BooksGrid";
import { useState, useEffect } from "react";

const SearchBooks = ({ books }) => {
    const [searchInput, setSearchInput] = useState("");
    const [searchResultBooks, setSearchResultBooks] = useState([]);

    useEffect(() => {
        setSearchResultBooks(books);
    }, []);

    useEffect(() => {
        const handleSearch = (value) => {
            console.log({ searchResultBooks });
            setSearchResultBooks(
            searchResultBooks.filter((book) => {
                console.log("value: ", value.toLowerCase())
                book.title.toLowerCase().includes(value.toLowerCase())
            }
            )
            );
        };

        handleSearch(searchInput);
    }, [searchInput])



    return (
        <div>
        <div className="search-books">
            <div className="search-books-bar">
            <Link className="close-search" to="/">
                Close
            </Link>
            <div className="search-books-input-wrapper">
                <input
                type="text"
                value={searchInput}
                placeholder="Search by title, author, or ISBN"
                onChange={(event) => searchInput(event.target.value)}
                />
            </div>
            </div>
            <div className="search-books-results">
            <BooksGrid books={books} />
            </div>
        </div>
        </div>
    );
    };

export default SearchBooks;
