import Book from "./Book";
import { useEffect, useState } from "react";

const Bookshelf = ({ title, books }) => {
  const [bookshelfBooks, setBookshelfBooks] = useState(books);

  useEffect( () => {
    setBookshelfBooks(books);
  }, [])


  const handleBookshelfUpdate = (book) => {
    setBookshelfBooks(
      bookshelfBooks.map((b) => {
        if (b.id === book.id) return (b.shelf = book.shelf);
      })
    );
  };

  return (
    <div className="bookshelf">
      <h1 className="bookshelf-title">{title}</h1>
      <div className="bookshelf-books">
        <ol className="books-grid">
          {bookshelfBooks.map((book) => {
            return (
              <div>
                <Book
                  book={book}
                  onBookshelfUpdate={(book) => handleBookshelfUpdate(book)}
                />
              </div>
            );
          })}
        </ol>
      </div>
    </div>
  );
};

export default Bookshelf;
