import * as BooksAPI from "./BooksAPI";
import { useEffect, useState } from 'react';

const BookshelfChanger = ({ book, onBookshelfUpdate }) => {
    const [selectedShelf, setSelectedShelf] = useState(book.shelf);

    const handleChange = async (value) => {
        const res = await BooksAPI.update(book, value);
        setSelectedShelf(value);       
        console.log(value);
        console.log("book.shelf: ", {...book, shelf: value})
        onBookshelfUpdate({...book, shelf: value});
    };

  return (
    <div className="book-shelf-changer">
      <select value={book.shelf} onChange={(event) => handleChange(event.target.value)}>
        <option value="none" disabled>
          Move to...
        </option>
        <option value="currentlyReading">Currently Reading</option>
        <option value="wantToRead">Want to Read</option>
        <option value="read">Read</option>
        <option value="none">None</option>
      </select>
    </div>
  );
};

export default BookshelfChanger;
