import Book from "./Book";
import { useEffect, useState } from "react";

const Bookshelf = ({ title, bookshelfCode, books, setBooks }) => {
  const [bookshelfBooks, setBookshelfBooks] = useState([]);

  useEffect(() => {
    console.log("⭐️")
    setBookshelfBooks(books.filter(
      (book) => book.shelf === bookshelfCode
    ));
  }, [books, bookshelfBooks]);

  const handleBookshelfUpdate = (book) => {
    const test = books.map((b) => {
      if (b.id === book.id) {
        b.shelf = book.shelf;
      }
      return b;
    });
    console.log({ test });
    setBooks(
      books.map((b) => {
        if (b.id === book.id) {
          b.shelf = book.shelf;
        }
        return b;
      })
    );
  };

  return (
    <div className="bookshelf">
      <h1 className="bookshelf-title">{title}</h1>
      <div className="bookshelf-books">
        <ol className="books-grid">
          {bookshelfBooks.map((book) => {
            return (
              <div>
                <Book
                  book={book}
                  onBookshelfUpdate={(book) => handleBookshelfUpdate(book)}
                />
              </div>
            );
          })}
        </ol>
      </div>
    </div>
  );
};

export default Bookshelf;
