import PropTypes from "prop-types";

import Book from "./Book";

const BooksGrid = ({ shelvedBooks, searchBooks, onBookshelfUpdate }) => {

  const mergeShelfData = () => {
    const searchBooksWithShelfData = [];
    for (const shelvedBook of shelvedBooks) {
        for (const searchBook of searchBooks) {
            console.log("Book: ", {searchBook})
            if (searchBook.id === shelvedBook.id) {
             searchBooksWithShelfData.push({ ...searchBook, shelf: shelvedBook.shelf });
             return null;
              }
               searchBooksWithShelfData.push(searchBook);
               return null;
        }
    };
    return searchBooksWithShelfData;
  }

  if (searchBooks)
  console.log(mergeShelfData())

  return (
    <ol className="books-grid">
      {!searchBooks
        ? shelvedBooks.map((book) => {
            return (
              <div key={book.id}>
                <Book book={book} onBookshelfUpdate={onBookshelfUpdate} />
              </div>
            );
          })
        : mergeShelfData().map((book) => {
            return (
              <div key={book.id}>
                <Book book={book} onBookshelfUpdate={onBookshelfUpdate} />
              </div>
            );
          })}
    </ol>
  );
};

BooksGrid.propTypes = {
  shelvedBooks: PropTypes.array.isRequired,
  searchBooks: PropTypes.array,
  onBookshelfUpdate: PropTypes.func,
};

export default BooksGrid;
