import Book from "./Book";
import { useEffect, useState } from "react";

const Bookshelf = ({ title, bookshelfCode, books, onBookshelfUpdate }) => {
  const [bookshelfBooks, setBookshelfBooks] = useState([]);

  useEffect(() => {
    setBookshelfBooks(books.filter((book) => book.shelf === bookshelfCode));
  }, [books]);

  return (
    <div className="bookshelf">
      <h1 className="bookshelf-title">{title}</h1>
      <div className="bookshelf-books">
        <ol className="books-grid">
          {bookshelfBooks.map((book) => {
            return (
              <div>
                <Book
                  book={book}
                  onBookshelfUpdate={(book) => onBookshelfUpdate(book)}
                />
              </div>
            );
          })}
        </ol>
      </div>
    </div>
  );
};

export default Bookshelf;
