import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";

import * as BooksAPI from "./BooksAPI";
import BooksGrid from "./BooksGrid";

const SearchBooks = ({ onBookshelfUpdate }) => {
  const [searchInput, setSearchInput] = useState("");
  const [showingResults, setShowingResults] = useState([]);

  useEffect(() => {}, [searchInput]);

  const handleSearch = (value) => {
    setShowingResults([]);
    const searchForBooks = async () => {
      const res = await BooksAPI.search(value);
      console.log({ res });
      setShowingResults(res);
    };
    setSearchInput(value);
    if (value !== null && value !== "") searchForBooks();
  };

  return (
    <div>
      <div className="search-books">
        <div className="search-books-bar">
          <Link className="close-search" to="/">
            Close
          </Link>
          <div className="search-books-input-wrapper">
            <input
              type="text"
              value={searchInput}
              placeholder="Search by title, author, or ISBN"
              onChange={(event) => handleSearch(event.target.value)}
            />
          </div>
        </div>
        <div className="search-books-results">
          {showingResults && showingResults.length > 0 && (
            <BooksGrid
              books={showingResults}
              onBookshelfUpdate={onBookshelfUpdate}
            />
          )}
        </div>
      </div>
    </div>
  );
};

export default SearchBooks;
