import { Routes, Route } from "react-router-dom";

import Bookshelf from "./Bookshelf";
import SearchBooks from "./SearchBooks";

const MyRead = ({ books, setBooks }) => {

  const handleBookshelfUpdate = (book) => {
    setBooks(
      books.map((b) =>
        (b.id === book.id) ? { ...b, shelf: book.shelf } : b
      )
    );
  };

  return (
    <Routes>
      <Route
        exact
        path="/"
        element={
          <div className="list-books">

            <div className="list-books-title">
              <h1>MyRead</h1>
            </div>

            <div className="list-books-content">
              <Bookshelf
                title="Currently Reading"
                bookshelfCode="currentlyReading"
                books={books}
                onBookshelfUpdate={handleBookshelfUpdate}
              />
              <Bookshelf
                title="Want To Read"
                bookshelfCode="wantToRead"
                books={books}
                onBookshelfUpdate={handleBookshelfUpdate}
              />
              <Bookshelf
                title="Read"
                bookshelfCode="read"
                books={books}
                onBookshelfUpdate={handleBookshelfUpdate}
              />
            </div>
          </div>
        }
      />
      <Route path="/search" element={<SearchBooks />} />
    </Routes>
  );
};

export default MyRead;
